using namespace std;

template&lt;class T&gt;
class Box {
private: T l; T b; T h;
public:
    Box(): l(0), b(0), h(0) {};
    Box(T length, T breadth, T height): l(length), b(breadth), h(height) {};
    Box(const Box&lt;T&gt;&amp; B): l(B.getL()), b(B.getB()), h(B.getH()) {};
    T getL() { return l; };
    T getB() { return b; };
    T getH() { return h; };
    T calV() { return l * b * h; };
    // 声明为 const 成员函数则是可行的
